import { handleBreadcrumb } from "../../funcs/utils.js";
import { mapMoveHandler } from "./utils.js";
const renderPagination = () => {
  const title = "پرداخت نهایی"
  const list = [
    {href: "./cart.html", value: "سبد خرید"},
    {href: "./checkout.html", value: "پرداخت نهایی"}
  ]

  handleBreadcrumb(title, list)
}

const renderMap = () => {
  let map = L.map("map").setView([35.715298, 51.404343], 13)
  
  let markerIcon = L.icon({
    iconUrl: "data:image/svg+xml;base64,",
    iconSize: [30, 30]
  });
  let mapMarker = L.marker([35.715298, 51.404343], {icon: markerIcon}).addTo(map)

  L.tileLayer("https://tile.openstreetmap.org/{z}/{x}/{y}.png", {
    maxZoom: 19,
  }).addTo(map);

  map.on("move", () => {
    mapMoveHandler(map, mapMarker)
  })
}

export{
  renderPagination,
  renderMap
}